package com.company.demo.repository;

import org.springframework.stereotype.Service;
import com.company.demo.domain.Legacy;
import com.company.demo.util.ValueGenerator;

/**
 * Helper class to create transient entities instance for testing purposes.
 * Simple properties are pre-filled with random values.
 */
@Service
public class LegacyGenerator {

    /**
     * Returns a new Legacy instance filled with random values.
     */
    public Legacy getLegacy() {
        Legacy legacy = new Legacy();

        legacy.setCode(ValueGenerator.getUniqueString(8));
        legacy.setDept(ValueGenerator.getUniqueInteger());
        legacy.setName(ValueGenerator.getUniqueString(16));
        // simple attributes follows
        legacy.setExtraInfo("a");
        return legacy;
    }

}